import { VdiUserJson } from './__json__/vdi-user-json';

/**
 * VDI User.
 */
/* istanbul ignore next: autogenerated */
export class VdiUser {

  constructor(private _json: VdiUserJson) {
  }

  /**
   * Get uuid.
   * @returns {string}
   */
  get uuid(): string {
    return this._json.uuid;
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get email.
   * @returns {string}
   */
  get email(): string {
    return this._json.email;
  }

  /**
   * Get the json representation of this class.
   * @returns {VdiUserJson}
   */
  get json(): VdiUserJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
